mapper.select:
select id id, resource_fusion_id resourceFusionId, resource_id resourceId, resource_organ_id resourceOrganId, operate_user_id operateUserId, user_id userId, audit_status auditStatus, assign_time assignTime, apply_time applyTime, c_time cTime, u_time uTime from data_resource_user_assign
and resource_id = #{resourceId}
and resource_fusion_id = #{resourceFusionId}
and audit_status = #{auditStatus}
and user_id = #{userId}
order by id desc limit #{offset}, #{pageSize}

