SELECT dr.resource_id as resourceId, dr.resource_name as resourceName, dr.resource_desc as resourceDesc, dr.resource_auth_type as resourceAuthType, dr.resource_source as resourceSource, dr.resource_num as resourceNum, dr.file_id as fileId, dr.file_size as fileSize, dr.file_suffix as fileSuffix, dr.file_columns as fileColumns, dr.file_rows as fileRows, dr.file_handle_field as fileHandleField, dr.file_handle_status as fileHandleStatus, dr.file_contains_y as fileContainsY, dr.file_y_rows as fileYRows, dr.file_y_ratio as fileYRatio, dr.resource_fusion_id as resourceFusionId, dr.public_organ_id as publicOrganId, dr.resource_state as resourceState, dr.resource_hash_code as resourceHashCode, dr.db_id as dbId, dr.user_id as userId, dr.organ_id as organId, dr.url as url, dr.create_date as createDate FROM data_resource dr left join data_resource_user_assign drua on dr.resource_id = drua.resource_id and drua.user_id = #{userId} where dr.is_del = 0 and drua.resource_id is null and dr.resource_source != 3
and dr.resource_state = #{resourceState}
and dr.resource_id = #{resourceId}
and dr.resource_auth_type = #{resourceAuthType}
and dr.resource_source = #{resourceSource}
and dr.resource_name like CONCAT('%',#{resourceName,jdbcType=VARCHAR},'%')
and dr.file_contains_y = 1
and dr.file_contains_y is null
and dr.resource_id in ( select DISTINCT rt.resource_id from data_resource_tag drt,data_rt rt where drt.tag_id = rt.tag_id
and drt.tag_name = #{tag}
and drt.tag_name like CONCAT('%',#{tag,jdbcType=VARCHAR},'%')
)
and dr.user_id in (select user_id from sys_user where is_del=0 and is_forbid=0 and user_name like CONCAT('%',#{userName,jdbcType=VARCHAR},'%'))
and cast( dr.file_handle_field as char) = #{fileHandleField}
and dr.user_id != #{userId}
ORDER BY dr.resource_id desc LIMIT #{offset},#{pageSize}

SELECT
count(*)
FROM
data_resource dr left join data_resource_user_assign drua on dr.resource_id = drua.resource_id and drua.user_id = #{userId}
where dr.is_del = 0 and drua.resource_id is null
and  dr.resource_source != 3

    and  dr.resource_state = #{resourceState}


    and  dr.resource_id = #{resourceId}


    and  dr.resource_auth_type = #{resourceAuthType}


    and  dr.resource_source = #{resourceSource}


    and  dr.resource_name like CONCAT('%',#{resourceName,jdbcType=VARCHAR},'%')



        and  dr.file_contains_y = 1


        and  dr.file_contains_y is null



    and  dr.resource_id in
    (
    select DISTINCT rt.resource_id from data_resource_tag drt,data_rt rt
    where drt.tag_id = rt.tag_id


            and drt.tag_name = #{tag}


            and drt.tag_name like CONCAT('%',#{tag,jdbcType=VARCHAR},'%')


    )


    and  dr.user_id in (select user_id from sys_user where is_del=0 and is_forbid=0 and user_name like
    CONCAT('%',#{userName,jdbcType=VARCHAR},'%'))


    and cast( dr.file_handle_field as char) = #{fileHandleField}


    and  dr.user_id != #{userId}

select id, resource_id resourceId, resource_fusion_id resourceFusionId, is_del isDel, create_date createDate, update_date updateDate, task_name taskName, task_id taskId, data_project_name dataProjectName, data_project_id dataProjectId, usage_time usageTime, usage_type usageType from data_resource_usage
and resource_id = #{resourceId}
and resource_fusion_id = #{resourceFusionId}
order by id DESC limit #{offset},#{pageSize}

