    count(id)
from data_resource_usage


        and resource_id = #{resourceId}


        and resource_fusion_id = #{resourceFusionId}

select dr.resource_id as resourceId, dr.resource_name as resourceName, dr.resource_desc as resourceDesc, dr.resource_auth_type as resourceAuthType, dr.resource_source as resourceSource, dr.resource_num as resourceNum, dr.file_id as fileId, dr.file_size as fileSize, dr.file_suffix as fileSuffix, dr.file_columns as fileColumns, dr.file_rows as fileRows, dr.file_handle_field as fileHandleField, dr.file_handle_status as fileHandleStatus, dr.file_contains_y as fileContainsY, dr.file_y_rows as fileYRows, dr.file_y_ratio as fileYRatio, dr.resource_fusion_id as resourceFusionId, dr.public_organ_id as publicOrganId, dr.resource_state as resourceState, dr.resource_hash_code as resourceHashCode, dr.db_id as dbId, dr.user_id as userId, dr.organ_id as organId, dr.url as url, dr.create_date as createDate from data_resource dr join data_resource_user_assign drua on dr.resource_fusion_id = drua.resource_fusion_id where dr.is_del = 0
and drua.resource_id = #{resourceId}
and drua.resource_fusion_id = #{resourceFusionId}
and drua.audit_status = #{auditStatus}
and drua.user_id = #{userId}
