mapper.select:
select from psi_record
where psi_name like CONCAT('%',#{taskName,jdbcType=VARCHAR},'%')
ORDER BY id desc LIMIT #{offset},#{pageSize}

select id, record_id recordId, pir_name pirName, pir_task_id pirTaskId, task_state taskState, origin_organ_id originOrganId, target_organ_id targetOrganId, start_time startTime, commit_rows_num commitRowsNum, end_time endTime, result_rows_num resultRowsNum, is_del isDel, create_date createDate, update_date updateDate from pir_record
where pir_name like CONCAT('%',#{taskName,jdbcType=VARCHAR},'%')
ORDER BY id desc LIMIT #{offset},#{pageSize}
